changeSet.sql:
create table ledger_receipt (
    signed boolean,
    type varchar(20),
    range_type varchar(20),
    range_begin timestamp,
    range_end timestamp,
    stamp_tax boolean,
    time timestamp,
    constraint ledger_ledger_receipt_pkey
        primary key (id)
);
create index on ledger_receipt (id);
create sequence ledger_receipt_seq start with 1 increment by 1;

create table ledger_ledger_receipt (
    ledger_id bigint not null
        constraint fk_ledger_ledger_receipt_ledger_id
            references ledger,
    ledger_receipt_id bigint not null
        constraint fk_ledger_ledger_receipt_ledger_receipt_id
            references ledger_receipt,
    constraint ledger_ledger_receipt_pkey
        primary key (ledger_id, ledger_receipt_id)
);
create index on ledger_receipt (lid, lrid);
create sequence ledger_ledger_receipt_seq start with 1 increment by 1;

create table ledger_receipt_printed_record (
    ledger_receipt_id bigint
        constraint fk_ledger_receipt_printed_record_lrid
            references ledger,
    time timestamp,
    constraint ledger_receipt_printed_record_pkey
        primary key (ledger_receipt_id, time)
);
create index on ledger_receipt (id);
create sequence ledger_receipt_printed_record_seq start with 1 increment by 1;
