changeSet.sql:
create table note (
  id bigint,
  type varchar(20),
  content varchar,
  patient_id bigint,
  user_id bigint,
  created_by varchar(50),
  created_date timestamp,
  last_modified_by varchar(50),
  last_modified_date timestamp
);
create sequence note_seq start with 1 increment by 1;
insert into note (id, patient_id, type, content) select nextval('note_seq'), id, 'SHARED', clinic_note from patient;
insert into note (id, patient_id, type, content) select nextval('note_seq'), id, 'SERVICE', note from patient;
